mapper.select:
SELECT role.*,sys.sysName as subSysName FROM SYS_ROLE role left join SYS_SUBSYSTEM sys on role.systemId=sys.systemId
order by role.${orderField} ${orderSeq}
order by role.roleId desc

SELECT role.*,sys.sysName as subSysName 
FROM SYS_ROLE role left join SYS_SUBSYSTEM sys on role.systemId=sys.systemId  
where role.enabled=1 
 AND role.systemId  =#{systemId} 
 AND role.alias  LIKE #{alias}  
 AND role.roleName  LIKE #{roleName}  

order by ${orderField} ${orderSeq}


order by roleId  desc
