mapper.update:
UPDATE sys_user_param SET userId=#{userId,jdbcType=NUMERIC} , paramId=#{paramId,jdbcType=NUMERIC} , paramValue=#{paramValue,jdbcType=VARCHAR} ,
paramIntValue=#{paramIntValue,jdbcType=NUMERIC} , paramDateValue=#{paramDateValue,jdbcType=DATE}
WHERE valueId=#{valueId}

mapper.select:
SELECT pv.valueId,pv.userId,pv.paramId,pv.paramValue,p.paramName,p.dataType
FROM SYS_USER_PARAM pv,SYS_PARAM p
WHERE 1=1
AND p.paramId=pv.paramId
 AND valueId  =#{valueId} 
 AND userId  =#{userId} 
 AND paramId  =#{paramId} 
 AND paramValue  LIKE #{paramValue} 

order by ${orderField} ${orderSeq}


order by paramId  desc

SELECT pv.valueId,pv.userId,pv.paramId,pv.paramValue,p.paramName,p.dataType,p.sourceType,p.sourceKey,p.status_,p.description
FROM SYS_USER_PARAM pv,SYS_PARAM p
WHERE 1=1
AND p.status_=1
AND p.paramId=pv.paramId
 AND valueId  =#{valueId} 
 AND userId  =#{userId} 
 AND paramId  =#{paramId} 
 AND paramValue  LIKE #{paramValue} 

order by ${orderField} ${orderSeq}


order by paramId  desc

SELECT pv.valueId,pv.userId,pv.paramId,pv.paramValue,p.paramName,p.dataType,pv.paramIntValue,pv.paramDateValue
FROM SYS_USER_PARAM pv,SYS_PARAM p
WHERE  p.paramId=pv.paramId AND pv.userId=#{userId} and p.paramKey=#{paramKey}
