 AND a.formDesc  LIKE #{formDesc}
 AND a.isDefault  =#{isDefault} 
 AND a.tableId  =#{tableId} 
 AND a.versionNo  =#{versionNo} 
 AND a.isPublished  =#{isPublished} 
 AND a.publishedBy  LIKE #{publishedBy}
 AND a.publishTime  =#{publishTime} 
 AND a.designType  =#{designType} 
 AND c.tableName  like #{tableName}

order by ${orderField} ${orderSeq}


	order by a.createtime desc

SELECT a.formDefId,a.formKey,a.categoryId,a.subject,a.formDesc,a.tabTitle,a.isDefault,a.tableId,a.versionNo,
a.isPublished,a.publishedBy,a.publishTime,a.designType,b.typename categoryName,c.tableName tableName  FROM bpm_form_def a left outer join  bpm_form_table c on a.tableId=c.tableId left outer join sys_gl_type b   on a.categoryid=b.typeid
where a.isDefault=1
 AND a.formDefId  =#{formDefId} 
 AND a.formKey  like #{formKey} 
 AND a.categoryId  =#{categoryId}  
 AND a.subject  LIKE #{subject}
 AND a.formDesc  LIKE #{formDesc}
 AND a.isDefault  =#{isDefault} 
 AND a.tableId  =#{tableId} 
 AND a.versionNo  =#{versionNo} 
 AND a.isPublished  =#{isPublished} 
 AND a.publishedBy  LIKE #{publishedBy}
 AND a.publishTime  =#{publishTime} 
 AND a.designType  =#{designType} 
 AND c.tableName  like #{tableName}

