SELECT distinct a.userId,a.fullname,a.account,a.entrydate,a.userStatus,c.orgName,e.posName FROM sys_user a left join sys_user_pos b on a.userid = b.userid and b.ISPRIMARY = 1 left join sys_org c on b.orgid = c.orgid left join sys_user_pos d on b.userid = d.userid and d.ISPRIMARY = 1 left join sys_pos e on d.posid = e.posid
order by ${orderField} ${orderSeq}
order by userId desc

		SELECT a.userId,a.fullname,a.account,a.entrydate,a.userStatus,b.userOrgId,c.orgName 
	    FROM  sys_user a 
	    join sys_user_pos b  on  a.userId=b.userId
	    join sys_org c on b.orgId=c.orgId
		where 1=1
		 AND c.path  LIKE '${path}%'  
		 AND a.fullname  LIKE #{fullname}  
		 AND a.account  LIKE #{account}  
         AND a.sex  LIKE #{sex}  
		 AND a.createtime>=#{begincreatetime}
		 AND a.createtime <=#{endcreatetime}

		order by ${orderField} ${orderSeq}


		order by a.userId  desc

