select * from ( SELECT DISTINCT a.userId,a.account, a.fullname,a.email,a.mobile,a.phone,a.entrydate,a.userStatus,5 retype FROM sys_user a inner join sys_user_pos b on a.userId=b.userId and b.isPrimary = 1 inner join sys_org c on b.orgId=c.orgId where a.isLock=0 and a.status=1 and c.path LIKE '${path}%' and b.isdelete=0 AND a.fullname LIKE #{fullname} ) tt
order by ${orderField} ${orderSeq}
order by userId desc

	select * from (		
	SELECT DISTINCT a.userId,a.fullname,a.email,a.mobile,a.phone,a.entrydate,a.userStatus,5 retype
    FROM  sys_user a 
    inner join   sys_user_pos b on  a.userId=b.userId
    inner join sys_org c on b.orgId=c.orgId
	where a.isLock=0 and a.status=1 and c.orgId =#{orgId} and b.isdelete =0 
	 AND a.fullname  LIKE #{fullname}  
	 ) tt

	order by ${orderField} ${orderSeq}


