mapper.select:
SELECT task.*,run.subject subject,run.processName processName,run.status taskStatus,run.globalFlowNo
FROM ACT_RU_TASK task left join BPM_PRO_RUN run
on task.PROC_INST_ID_=run.actInstId
left join bpm_definition def on task.PROC_DEF_ID_=def.actDefId
where 1=1 and def.status!=3
 AND task.id_ LIKE #{id} 
 AND task.name_ LIKE #{name} 
 AND task.assignee_  in 

		#{assigneeItem}






 		AND def.defKey in(${actRights})  


 		AND def.defKey = ''  



 AND run.status = #{taskStatus} 
 AND task.create_time_  >=#{createTime,jdbcType=TIMESTAMP} 
and task.create_time_ <=#{endCreateTime,jdbcType=TIMESTAMP}
 AND run.subject LIKE #{subject} 
 AND run.processName LIKE #{processName} 
 AND run.CREATORID = #{createId} 
 AND run.GLOBALFLOWNO LIKE #{globalFlowNo} 


	order by task.CREATE_TIME_ desc


	order by ${orderField} ${orderSeq}

		 select distinct * from
	    (
	          WHERE task.ASSIGNEE_ = #{userId} and task.DESCRIPTION_!='39' and def.status!=3

	         UNION ALL

	           		JOIN act_ru_identitylink ident ON task.id_ = ident.TASK_ID_
	          WHERE ident.USER_ID_ = #{userId} 
	          		and task.ASSIGNEE_ =0 and task.DESCRIPTION_!='39' and def.status!=3

	          UNION ALL


             	  JOIN act_ru_identitylink ident ON task.id_ = ident.TASK_ID_
            	WHERE ident.type_ = 'org' and ident.group_id_ in (select uo.orgid from SYS_USER_POS uo  where uo.userid= #{userId} )
                and task.ASSIGNEE_ =0 and task.DESCRIPTION_!='39' and def.status!=3

                UNION ALL


             	  JOIN act_ru_identitylink ident ON task.id_ = ident.TASK_ID_
            	WHERE ident.type_ = 'role' and ident.group_id_ in (select ur.roleid from sys_user_role ur where ur.userid= #{userId} )
                and task.ASSIGNEE_ =0 and task.DESCRIPTION_!='39' and def.status!=3

                 UNION ALL


             	  JOIN act_ru_identitylink ident ON task.id_ = ident.TASK_ID_
            	WHERE ident.type_ = 'pos' and ident.group_id_ in (select up.posid from sys_user_pos up where up.userid= #{userId} and up.isdelete=0)
                and task.ASSIGNEE_ =0 and task.DESCRIPTION_!='39'  and def.status!=3

                 UNION ALL


             	  JOIN act_ru_identitylink ident ON task.id_ = ident.TASK_ID_
            	WHERE ident.type_ = 'job' and ident.group_id_ in (select p.jobid from SYS_USER_POS uo inner join sys_pos p on p.posid=uo.posid  where uo.userid= #{userId} )
                and task.ASSIGNEE_ =0 and task.DESCRIPTION_!='39'  and def.status!=3
	           )
	  		  tmp

				 AND upper(tmp.subject) LIKE #{subject}  ESCAPE '|' 
				 AND upper(tmp.processName) LIKE #{processName} 
				AND tmp.nodePath like #{nodePath} 
				AND tmp.creatorId =#{creatorId} 
				and tmp.CREATE_TIME_>=#{beginCreateTime,jdbcType=TIMESTAMP}
				and tmp.CREATE_TIME_ <=#{endCreateTime,jdbcType=TIMESTAMP}
				AND tmp.NAME_ = #{name} 

					AND tmp.description_ = #{type}


					AND tmp.taskStatus = #{status}

				 AND tmp.hasRead =#{hasRead} 


				order by tmp.PRIORITY_ desc, tmp.CREATE_TIME_ desc 


				order by ${orderField} ${orderSeq}

select * from ( SELECT task.*, run.createtime createDate, run.subject subject, run.processName processName, run.creator, run.creatorId FROM ACT_RU_TASK task left join BPM_PRO_RUN run on task.PROC_INST_ID_=run.actInstId where task.ASSIGNEE_ = #{userId} union all SELECT task.*, run.createtime createDate, run.subject subject, run.processName processName, run.creator, run.creatorId FROM ACT_RU_TASK task left join BPM_PRO_RUN run on task.PROC_INST_ID_=run.actInstId left join act_ru_identitylink ident on task.id_=ident.TASK_ID_ where ident.USER_ID_ = #{userId} and task.ASSIGNEE_ =0 ) result
AND subject LIKE #{subject}
order by CREATE_TIME_ desc
order by ${orderField} ${orderSeq}

SELECT  task.*,run.subject subject,run.processName processName
FROM ACT_RU_TASK task left join BPM_PRO_RUN run
ON task.PROC_INST_ID_=run.actInstId where task.ID_=#{taskId}
